import React, { useState } from 'react'
import './Pages.css'
function Pages() {
    const [selected, setSelected] = useState(0)

    return (
        <div className='d-flex'>
            <div className='pageIndicator' onClick={e => setSelected((selected++) % 5)}><i class="fa fa-solid fa-chevron-left"></i></div>
            {
                Array(1, 2, "..", 9, 10)
                    .map((elem, i) =>
                        i == selected ?
                            <div className='pageIndicator pageSelected'>{elem}</div>
                            :
                            <div className='pageIndicator'>{elem}</div>
                    )
            }
            <div className='pageIndicator' onClick={e => setSelected((selected--) % 5)}><i class="fa fa-solid fa-chevron-right"></i></div>
        </div>
    )
}

export default Pages
