import React, { useEffect, useState } from 'react'
import { PieChart } from '@mui/x-charts/PieChart';
import { getPieChartData } from '../api';
import './Pie.css'

function Pie() {
    const [pieData, setPieData] = useState([])
    useEffect(() => {
        getPieChartData()
            .then(res => {
                console.log("Pie chart data")
                console.log(res)
                setPieData(res)
            })
        return () => { }
    }, [])
    return (
        <PieChart
            colors={['#D0FFD0', '#C0FEC0', '#B0FAB0', '#A0F4A0', '#90EE90']} // Use palette
            series={[{ data: pieData }]}
            width={400}
            height={250}
            className='ms-auto me-auto'
            // margin={{ top: 0, bottom: -10, left: -20, right: 0 }}
            slotProps={{
                legend: {
                    direction: 'column',
                    classes: 'mt-5',
                    position: {
                        vertical: 'bottom',
                        horizontal: 'left',
                    }, 
                    labelStyle: {position:'absolute',top:0},
                    itemMarkWidth: 10,
                    itemMarkHeight: 10,
                    itemMarkRadius: 10,
                    markGap: 5,
                    itemGap: 10,
                }
            }}
        />
    )
}

export default Pie
