const SERVER = "http://localhost:3001/api"
export const getTableData = () => {
    // return new Promise((resolve, reject) => {
    // })
    fetch(SERVER + '/table')
        .then(res => {
            return res.json()
        })
        .catch(err => {
            console.log(err)
            return err
        })
}
export const getPieChartData = () => {
    // return new Promise((resolve, reject) => {
    // })
    fetch(SERVER + '/pie-chart')
        .then(res => {
            return res.json()
        })
        .catch(err => {
            console.log(err)
            return err
        })
}
export const getGraphData = () => {
    // return new Promise((resolve, reject) => {
    // })
    fetch(SERVER + '/graph')
        .then(res => {
            return res.json()
        })
        .catch(err => {
            console.log(err)
            return err
        })
}
